import java.util.*;
public class Que_3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		Scanner sc = new Scanner(System.in);
		
		int a = sc.nextInt();
		int n=5;
		
		for (int i=0;i<=a;i++) {
			for (int j=0;j<n;j++) {
				System.out.println(" ");
			}
			System.out.println("*");
		}
	}

}
